package src;public class MataKuliah{	private String kode;	private String nama;	private Integer sks;		public void ubahKode(String kode){		this.kode=kode;	}	public String ambilKode(){		return kode;	}		public void ubahNama(String namaBaru){		nama=namaBaru;	}	public String ambilNama(){		return nama;	}	public void ubahSks(Integer sks){		this.sks=sks;	}	public Integer ambilSks(){		return sks;	}}